import React, {Component} from 'react';
import Panelbox from './Panelbox'

class Create extends Component{

    state = {
        products:{
            productName:"",
            productCode:"",
            price:"",
            releaseDate:"",
            starRating:""
        }
    }

    createProducts = (event) =>{
        event.preventDefault();
        alert("Hello");
    }

    render(){


        const content = (
            <form>
                <div className="form-group">
                    <label className="control-label">Product Name</label>
                    <input type="text" placeholder="Product Name" name="productName" className="form-control"/>
                </div>

                <div className="form-group">
                    <label className="control-label">Product Code</label>
                    <input type="text" placeholder="Product Code" name="productCode" className="form-control"/>
                </div>


                <div className="form-group">
                    <label className="control-label">Price</label>
                    <input type="text" placeholder="Price" name="price" className="form-control"/>
                </div>



                <div className="form-group">
                    <label className="control-label">Release</label>
                    <input type="text" placeholder="Release" name="releaseDate" className="form-control"/>
                </div>


                <div className="form-group">
                    <label className="control-label">Rating</label>
                    <input type="text" placeholder="Rating" name="starRating" className="form-control"/>
                </div>

                <button className="btn btn-success" onClick={this.createProducts}>Create</button>
            </form>


        )
        return  <Panelbox heading="Create" content={content}/>

    }
}

export default Create;
