import React,{Component} from 'react';

class Products extends Component{

    state = {
        loadimage: true
    }

    toggleImage = () => {
        this.setState((prevState) =>{
           return {
               loadimage: !prevState.loadimage
           }
        });
    }

    render()
{
        return <div className="panel panel-primary">
            <div className="panel panel-heading">Products</div>
            <div className="panel panel-body">
                <table className="table table-bordered">
                    <thead>
                    <tr>
                        <th>
                        <button className="btn btn-primary" onClick={this.toggleImage}>
                            {
                                this.state.loadimage ? "Hide Image" : "Show Image"
                            }
                        </button>
                        </th>
                        <th>Product Name</th>
                        <th>Product Code</th>
                        <th>Price</th>
                        <th>Release</th>
                        <th>Rating</th>
                    </tr>
                    </thead>

                    <tbody>
                    {
                        this.props.product.map((item, index) => (
                            <tr key={index}>
                                <td>
                                    <img alt="" src={item.imageUrl} style={styles.image}/>
                                </td>

                                <td>{item.productName}</td>
                                <td>{item.productCode}</td>
                                <td>{item.price}</td>
                                <td>{item.releaseDate}</td>
                                <td>{item.starRating}</td>
                                <td>
                                    <button className="btn btn-danger">Remove</button>
                                </td>
                                </tr>
                        ))
                    }
                    </tbody>

                </table>

            </div>
        </div>
    }
}

const styles = {
        image:{
            width:"50px"
        }
}

export default Products;
